package com.lm2a.controller;import java.util.ArrayList;import java.util.List;import java.util.stream.Collectors;import javax.annotation.PostConstruct;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.github.javafaker.Faker;@RestController@RequestMapping("/api/v1/gameofthrones")
public class CharactersController {		private Faker faker = new Faker();	private List<String> characters = new ArrayList<>();		@PostConstruct	private void init() {		for(int i=0; i<10; ++i) {			characters.add(faker.gameOfThrones().character());		}	}		@GetMapping("/characters")	public 	List<String> getAllCharacters(){		return characters;	}		@GetMapping("/{name}")	public String getCharacter(@PathVariable String name) {		return characters.stream().filter(x -> x.contains(name)).findFirst().toString();	}		@GetMapping("/prefix/{prefix}")	public List<String> getCharacterWithPrefix(@PathVariable String prefix){		return characters.stream().filter(x->x.startsWith(prefix)).collect(Collectors.toList());	}	
}
